import '../index.css';
import React from 'react';
import api from '../utils/Api';
import { Card } from './Card';

export function Main(props) {
    const [data, setData] = React.useState({
        name: "",
        about: "",
        avatar: false,
    });
    React.useEffect(() => {
        api
        .getCurrentUser()
        .then((data) => {
            setData({
                name: data.name,
                about: data.about,
                avatar: data.avatar
            })
        })
        .catch((err) => {
            console.log(err);
        });
    });

    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api
            .getCards()
            .then((data) => {
                setCards(data);
            })
            .catch((err) => {
                console.log(err);
            })
    }, []);

    return (
        <main className={Main}>
            <section className="profile">
                <button type="button" className="profile__avatar-button" onClick={props.onEditAvatar}>
                    <img src={data.avatar} alt="Аватар" className="profile__avatar" />
                </button>
                <div className="profile__info">
                    <div className="profile__text">
                        <h1 className="profile__title">{data.name}</h1>
                        <p className="profile__subtitle">{data.about}</p>
                    </div>
                    <button type="button" className="profile__edit-button" onClick={props.onEditProfile}></button>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>
            </section>
            <section className="elements" aria-label="фотокарточки">
                {cards.map((card) => (
                        <Card key={card.id} {...card} onCardClick={props.onCardClick}/>
                    ))}
            </section>

            <div className="popup popup_delete_accept">
                <div className="popup__container">
                    <h2 className="popup__title">Вы уверены?</h2>
                    <button type="button" className="popup__save-button">Да</button>
                    <button type="button" className="popup__close-button"></button>
                </div>
            </div>


        </main>
    );
}
