import React from 'react';
import usePopupClose from '../hooks/usePopupClose';

export function PopupWithForm(props) {
    usePopupClose(isOpen, onClose);

    return (
        <div className={`popup popup_name_${props.name} ${props.isOpen ? `popup_opened` : ""}`}>
                <div className="popup__container">
                    <h2 className="popup__title">{props.title}</h2>

                    <form className="popup__form" name={props.form} noValidate onSubmit={props.onSubmit}>
                        {props.children}
                        <button type="submit" className="popup__save-button">{props.buttonText}</button>
                    </form>

                    <button type="button" className="popup__close-button" onClick={props.onClose}></button>
                </div>
            </div>
    );
};
