import React from 'react';
import { Header } from './Header';
import { Main } from './Main';
import { Footer } from './Footer';
import { PopupWithForm } from './PopupWithForm';
import { ImagePopup } from './ImagePopup';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isDeletePopupOpen, setIsDeletePopupOpen] = React.useState(false);

  const [selectedCard, setSelectedCard] = React.useState(null);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleDeleteClick() {
    setIsDeletePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsDeletePopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  return (
    <div className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onDelete={handleDeleteClick}
        onCardClick={handleCardClick}
      />
      <Footer />
      <PopupWithForm
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        title={"Редактировать профиль"}
        buttonText={"Сохранить"}
        name={"profile"}
        form={"profileForm"}
      >
        <input type="text" className="popup__input popup__input_type_names" id="name-input" name="name"
          placeholder="Имя" minLength="2" maxLength="40" required />
        <span className="popup__input-error name-input-error"></span>
        <input type="text" className="popup__input popup__input_type_description" id="description-input"
          name="about" placeholder="Описание" minLength="2" maxLength="200" required />
        <span className="popup__input-error description-input-error"></span>
      </PopupWithForm>

      <PopupWithForm
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        title={"Обновить аватар"}
        buttonText={"Сохранить"}
        name={"avatar"}
        form={"avatarForm"}
      >
        <input type="url" className="popup__input popup__input_type_avatar-link" id="avatarlink-input" name="avatar"
          placeholder="Ссылка на аватар" required />
        <span className="popup__input-error avatarlink-input-error"></span>
      </PopupWithForm>

      <PopupWithForm
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        title={"Новое место"}
        buttonText={"Создать"}
        name={"card"}
        form={"editForm"}
      >
        <input type="text" className="popup__input popup__input_type_card-name" id="names-input" name="name"
          placeholder="Название" minLength="2" maxLength="40" required />
        <span className="popup__input-error names-input-error"></span>
        <input type="url" className="popup__input popup__input_type_card-link" id="imglink-input" name="link"
          placeholder="Ссылка на картинку" required />
        <span className="popup__input-error imglink-input-error"></span>
      </PopupWithForm>

      <PopupWithForm
        isOpen={isDeletePopupOpen}
        onClose={closeAllPopups}
        title={"Вы уверены?"}
        buttonText={"Да"}
        name={"delete"}
      />

      <ImagePopup
        isOpen={selectedCard}
        card={selectedCard}
        onClose={closeAllPopups}
      />
    </div>
  );
}

export default App;
