import '../index.css';
import React from 'react';
import api from '../utils/Api';

export function Main(props) {
    const [data, setData] = React.useState({
        name: "",
        about: "",
        avatar: false,
    });
    React.useEffect(() => {
        api
        .getCurrentUser()
        .then((data) => {
            setData({
                name: data.name,
                about: data.about,
                avatar: data.avatar
            })
        })
        .catch((err) => {
            console.log(err);
        });
    });

    return (
        <main>
            <section className="profile">
                <button type="button" className="profile__avatar-button" onClick={props.onEditAvatar}>
                    <img src={data.avatar} alt="Аватар" className="profile__avatar" />
                </button>
                <div className="profile__info">
                    <div className="profile__text">
                        <h1 className="profile__title">{data.name}</h1>
                        <p className="profile__subtitle">{data.about}</p>
                    </div>
                    <button type="button" className="profile__edit-button" onClick={props.onEditProfile}></button>
                </div>
                <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>
            </section>
            <section className="elements" aria-label="фотокарточки">

            </section>

            <template className="element-template">
                <li className="element">
                    <button type="button" className="element__delete-button"></button>
                    <img src="#" alt="Фото" className="element__image" />
                    <div className="element__description">
                        <h2 className="element__title"></h2>
                        <div className="element__like-container">
                            <button type="button" className="element__button"></button>
                            <p className="element__like-number"></p>
                        </div>
                    </div>
                </li>
            </template>

            <div className="popup popup_delete_accept">
                <div className="popup__container">
                    <h2 className="popup__title">Вы уверены?</h2>
                    <button type="button" className="popup__save-button">Да</button>
                    <button type="button" className="popup__close-button"></button>
                </div>
            </div>


        </main>
    );
}
